    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int index = jComboBox1.getSelectedIndex();
        int index2 = jComboBox2.getSelectedIndex();
        String value = jTextPane1.getText();
        if (index==0&&index2==0) { //desetiško v dvojiško
            int res = Integer.parseInt(value);
            jLabel6.setText(Integer.toString(res,2));
        }
        if (index==1&&index2==1) { //dvojiško v desetiško
            int res = Integer.parseInt(value, 2);
            jLabel6.setText(String.valueOf(res));
        }
        if (index==1&&index2==0) { //dvojiško v dvojiško
            jLabel6.setText(value);
        }
        if (index==0&&index2==1) { //dvojiško v dvojiško
            jLabel6.setText(value);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

