    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        int index = jComboBox4.getSelectedIndex();
        int index2 = jComboBox3.getSelectedIndex();
        String value = jTextPane2.getText();
        int decvalue = (int)value.charAt(0);
        String value2 = Integer.toString(decvalue);
        if (index==0&&index2==0) { //ASCII v ASCII
            jLabel5.setText(value);
        }
        if (index==0&&index2==1) { //ASCII v dvojiško
            jLabel5.setText(Integer.toString(decvalue,2));
        }
        if (index==0&&index2==2) { //ASCII v osmiško
            jLabel5.setText(Integer.toString(decvalue,8));
        }
        if (index==0&&index2==3) { //ASCII v šestnajstiško
            jLabel5.setText(Integer.toString(decvalue,16));
        }
        if (index==1&&index2==0) { //dvojiško v ASCII
            int res = Integer.parseInt(value2, 2);
            jLabel5.setText(String.valueOf(res));
        }
        if (index==2&&index2==0) { //osmiško v ASCII
            int res = Integer.parseInt(value2, 8);
            jLabel5.setText(String.valueOf(res));
        }
        if (index==3&&index2==0) { //šestnajstiško v ASCII
            int res = Integer.parseInt(value2, 16);
            jLabel5.setText(String.valueOf(res));
        } 
    }//GEN-LAST:event_jButton2ActionPerformed

